<?phprequire_once('wp_bootstrap_navwalker.php');require_once('customize-theme.php');add_theme_support( 'post-thumbnails' );function register_my_menus() {  register_nav_menus(    array(		'primary' => __( 'Header Navigation', 'hardnews' ),  		'secondary1' => __('Footer Navigation 1', 'hardnews'),  		'secondary2' => __('Footer Navigation 2', 'hardnews'),  		'secondary3' => __('Footer Navigation 3', 'hardnews')    )  );}add_action( 'init', 'register_my_menus' );/*remove extra <p> and <br> tags*/remove_filter( 'the_content', 'wpautop' );remove_filter( 'the_excerpt', 'wpautop' );/* load javascript */function load_js() {	wp_deregister_script('jquery');	wp_register_script('jquery', get_template_directory_uri() . '/js/jquery3.2.1.min.js', array(), null, true);	wp_enqueue_script( 'custom-script', get_template_directory_uri() . '/js/script.js', array('jquery'), null, true );	wp_enqueue_script( 'boostrap', get_template_directory_uri() . '/js/bootstrap.min.js', array('jquery'), null, true );	//enqueue style	wp_enqueue_style('hn-bootstrap', get_template_directory_uri().'/css/bootstrap.min.css', false, get_stylesheet_directory() . '/css/bootstrap.min.css' );	wp_enqueue_style('hn-font-awesome', get_template_directory_uri().'/css/font-awesome-4.7.0/css/font-awesome.min.css', false, get_stylesheet_directory() . '/css/font-awesome-4.7.0/css/font-awesome.min.css' );	wp_enqueue_style('hn-google-fonts', 'https://fonts.googleapis.com/css?family=Montserrat|Cardo', false );	wp_enqueue_style( 'main-style', get_template_directory_uri() .'/style.css', false, filemtime(get_stylesheet_directory() . '/style.css') );}add_action('wp_enqueue_scripts', 'load_js');function custom_excerpt_length( $length ) {	return 20;}add_filter( 'excerpt_length', 'custom_excerpt_length', 999 );add_filter('posts_query_atts', function( $atts ){	$atts['title'] = '';	return $atts;});add_filter('show_admin_bar', '__return_false');